	dynamic 
Where 
	dynamicUserID in (SELECT agreeID from relation WHERE type=1 AND applyID = #{0}  
						UNION SELECT applyID from relation WHERE type=1 AND agreeID = #{0} ) 
ORDER by dynamicTime DESC LIMIT #{1},#{2}

